    /**
     * The vat method
     *
     * Returns a random Romanian VAT number
     *
     * @example 'RO37864855'
     *
     * @return string
     */
    public function vat() {
        if (rand (0,1)) {
            return $this->validVat ();
        }
        return $this->safeVat();
    }
    
    /**
     * The validVat method
     *
     * Returns a Romanian VAT number in a valid format
     * Attention: this method may return a VAT number associated with an active company in Romania
     *
     * @example 'RO37864855'
     *
     * @return string
     */
    public function validVat() {
        $vat = static::randomNumber (8);
        if (!$this->isValid ($vat)) {
            return $this->validVat ();
        }
        return (rand (0,1) ? 'RO' : '') . $vat;
    }
    
    /**
     * The safeVat method
     *
     * Returns an invalid Romanian VAT number
     *
     * @example 'RO37864856'
     *
     * @return string
     */
    public function safeVat() {
        $vat = static::randomNumber (8);
        if ($this->isValid ($vat)) {
            return $this->safeVat ();
        }
        return (rand (0,1) ? 'RO' : '') . $vat;
    }
    
    /**
     * Valid Romanian VAT number checker
     *
     * Source, slightly modified, see first comment: https://gabrielsolomon.ro/validare-cuicif-folosind-php/
     *
     * @param integer $vat A positive integer
     *
     * @return bool
     */
    private function isValid($vat) {
        $validationKey = str_split(strrev("753217532"));
        $vatArray = str_split(strrev($vat));
        $sum = 0;
        for($i=1;$i < count($vatArray);$i++)
            $sum += $vatArray[$i]*$validationKey[$i-1];
        $rest = ($sum * 10 % 11 == 10)? 0 : $sum * 10 % 11;
        return $rest == $vatArray[0];
    }
